function $(){for(var e=new Array,t=0;t<arguments.length;t++){var n=arguments[t];if("string"==typeof n&&(n=document.getElementById(n)),1==arguments.length)return n;e.push(n)}return e}// and now our improved toggler!
function toggle(e){var t=$(e);t.style.display="none"!=t.style.display?"none":"",document.getElementById("hamburger").style.visibility=""!=document.getElementById("hamburger").style.visibility?"":"hidden"}